import React,{useState} from "react"
import Axios from "axios"
import './style.css'
const AddProduct = () => {
const [name,setName]=useState("");
const [quantity,setQuantity]=useState(0);
const [price,setPrice]= useState(0);
  

const addToList=async() =>{
  try{
   const addedProduct= await Axios.post("http://localhost:5000/add",{name: name, quantity:quantity,price: price})
    console.log("added with sucess",addedProduct)
}catch(err){
  console.log(err)
}
 }
 return (
    <div className="form">
     <label>Product Type</label>
    <input type="text" onChange={(event)=>{setName(event.target.value)}}/>
    <label>Quantity</label>
    <input type="number" onChange={(event)=>{setQuantity(event.target.value)}}/>
    <label>Price</label>
    <input type="text" onChange={(event)=>{setPrice(event.target.value)}}/>
    <button onClick={()=>addToList()}> Add to List</button>
    </div>
  )
}

export default AddProduct
