import React from 'react'
import Axios from "axios"
import {useParams} from 'react-router'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style.css'

const ProductCard = ({card:{name,quantity,price}}) => {
      const {id} = useParams()

   const deleteFromList=async(id) =>{
      return await Axios.delete(`http://localhost:5000/delete/${id}`)
      
        }
  return (
        <div className="card">
        <div className="card-description">
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h1 style={{marginTop:'10px', marginLeft:'20px'}}> {name}</h1>
        </div>
        <h4>Stock : {quantity}</h4>
        <h4> Price :{price} TND</h4>  
        <button onClick={()=>deleteFromList()}>Delete</button>   
        </div>    
        </div>    

  )
}

export default ProductCard
